#include <stdio.h>
#include <stdlib.h>
#include <stddef.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include "get_next_line.h"

int main(void)
{
	int		fd;
	char	*line;

	fd = open("./test_for_gnl.txt", O_CREAT | O_WRONLY | O_TRUNC, 0664);
	write(fd, "aaaaaaaaaaaaaaaaaaaa\n", 21);
	write(fd, "bbbbbbbbbbbbbbbbbbbb\n", 21);
	write(fd, "cccccccccccccccccccc\n", 21);
	write(fd, "dddddddddddddddddddd\n", 21);
	write(fd, "eeeeeeeeeeeeeeeeeeee\n", 21);
	write(fd, "\n", 401);
	write(fd, "\n", 401);
	write(fd, "\n", 401);
	write(fd, "\n", 401);
	write(fd, "\n", 401);
	close(fd);
	fd = open("./test_for_gnl.txt", O_RDONLY);
	if (fd < 0)
		return (-1);
	line = NULL;
	while (line = get_next_line(fd))
	{
		printf("%s", line);
		free(line);
	}
	close(fd);
	return (0);
}
